    /**
     * output getSelectors method
     */
    public SelectorItemCollection getSelectors()
    {
        SelectorItemCollection sic = new SelectorItemCollection();
		String selectorAll = System.getProperty("selector.all");
		if(StringUtils.isEmpty(selectorAll)){
			selectorAll = "true";
		}
        sic.add(new SelectorItemInfo("description"));
		if(selectorAll.equalsIgnoreCase("true"))
		{
			sic.add(new SelectorItemInfo("entry.*"));
		}
		else{
		}
		if(selectorAll.equalsIgnoreCase("true"))
		{
			sic.add(new SelectorItemInfo("entry.sellProject.*"));
		}
		else{
	    	sic.add(new SelectorItemInfo("entry.sellProject.id"));
			sic.add(new SelectorItemInfo("entry.sellProject.name"));
        	sic.add(new SelectorItemInfo("entry.sellProject.number"));
		}
		if(selectorAll.equalsIgnoreCase("true"))
		{
			sic.add(new SelectorItemInfo("entry.customer.*"));
		}
		else{
	    	sic.add(new SelectorItemInfo("entry.customer.id"));
			sic.add(new SelectorItemInfo("entry.customer.name"));
        	sic.add(new SelectorItemInfo("entry.customer.number"));
		}
		if(selectorAll.equalsIgnoreCase("true"))
		{
			sic.add(new SelectorItemInfo("entry.room.*"));
		}
		else{
	    	sic.add(new SelectorItemInfo("entry.room.id"));
			sic.add(new SelectorItemInfo("entry.room.name"));
        	sic.add(new SelectorItemInfo("entry.room.number"));
		}
    	sic.add(new SelectorItemInfo("entry.sellAmount"));
    	sic.add(new SelectorItemInfo("entry.backAmount"));
    	sic.add(new SelectorItemInfo("entry.sellProject.orgUnit.name"));
		if(selectorAll.equalsIgnoreCase("true"))
		{
			sic.add(new SelectorItemInfo("creator.*"));
		}
		else{
        	sic.add(new SelectorItemInfo("creator.id"));
        	sic.add(new SelectorItemInfo("creator.number"));
        	sic.add(new SelectorItemInfo("creator.name"));
		}
        sic.add(new SelectorItemInfo("createTime"));
		if(selectorAll.equalsIgnoreCase("true"))
		{
			sic.add(new SelectorItemInfo("auditor.*"));
		}
		else{
        	sic.add(new SelectorItemInfo("auditor.id"));
        	sic.add(new SelectorItemInfo("auditor.number"));
        	sic.add(new SelectorItemInfo("auditor.name"));
		}
        sic.add(new SelectorItemInfo("auditTime"));
        sic.add(new SelectorItemInfo("number"));
        sic.add(new SelectorItemInfo("name"));
        sic.add(new SelectorItemInfo("bizDate"));
        return sic;
    }        
    	

